import { useEffect, useState } from "react";
import React from "react-dom";
import { getTracker } from "../helpers/getTracker";
import { useCurrentUser } from "./CurrentUserContext";
import NavBar from "./NavBar";
import Footer from "./Footer";



const url = process.env.BACK_END_URL || "http://localhost:8000";


function Trades() {
    const [trades, setTrades] = useState([]);
    const { currentUser } = useCurrentUser();

    useEffect(() => {
        setTrades([])
        const fetchTrades = async () => {
            try {
                const response = await getTracker(
                    currentUser.token,
                    "trades",
                    url
                );
                if (response.detail !== "Invalid token.") {
                    response.forEach((trade) => {
                        trade.start = trade.start.substring(0, 10);
                        trade.end = trade.end.substring(0, 10);
                        // (date.replace("T", " ").replace("Z", "")) for details of the trade
                        setTrades((prevTrade) => [...prevTrade, trade]);
                    });
                }
            } catch (e) {
                console.log(e);
            }
        };
        fetchTrades();
        // i disable the props.url warning
        // eslint-disable-next-line react-hooks/exhaustive-deps
    }, [currentUser,]);

    const [color, setColor] = useState()

    return (
        <div className="trades">
            <div class="page-wrap">
                <NavBar />
                    
                    <div class="section">
                        <div className="title">Trades</div>
                        <div className="table">
                            <table cellspacing="0">
                                <tr>
                                    <th align="center">
                                        Pair
                                    </th>
                                    <th align="center" class="date">
                                        Start
                                    </th>
                                    <th align="center" class="date">
                                        End
                                    </th>
                                    <th align="right" class="p-l">
                                        Profit/Loss
                                    </th>
                                    <th align="right" class="prctg">
                                        Percentage
                                    </th>
                                </tr>

                                {trades.map((trade) => {
                                    return (
                                        <tr key={trade.id}>
                                            <td align="center" class="pair">
                                                <img width="30px" height="30px" src={trade.logo} alt="coin logo" />
                                                {trade.pair}
                                            </td>
                                            <td align="center" class="date">
                                                {trade.start}
                                            </td>
                                            <td align="center" class="date">
                                                {trade.end}
                                            </td>
                                            <td align="right" class={color}>
                                                ${trade.profit_loss.toFixed(2)}
                                            </td>
                                            <td align="right" class={color}>
                                                {trade.percentage.toFixed(2)}%
                                            </td>
                                        </tr>
                                    );
                                })}
                            </table>
                        </div>
                    </div>
                   
                </div>
            <Footer />
        </div>
    );
}

export default Trades;
