import { useState } from "react";
import userSvg from "../svg/user-svg.svg"

function NavBar() {
    const [cls, setCls] = useState({
        nav: "nav-menu",
        ham: "hamburger",
        b1: "bar",
        b2: "bar",
        b3: "bar",
    });

    function mobileMenu() {
        if (cls.nav === "nav-menu") {
            setCls({
                nav: "nav-menu active",
                ham: "hamburger active",
                b1: "bar b1",
                b2: "bar b2",
                b3: "bar b3",
            });
        } else {
            setCls({
                nav: "nav-menu",
                ham: "hamburger",
                bar: "bar",
                b1: "bar",
                b2: "bar",
                b3: "bar",
            });
        }
    }

    const navLink = document.querySelectorAll(".nav-link");

    // navLink.forEach(n => n.addEventListener("click", closeMenu));

    return (
        <header className="header">
            <nav className="navbar">
                <ul className={cls.nav}>
                    <li className="nav-item">
                        <a href="#" className="nav-link">
                            Home
                        </a>
                    </li>
                    <li className="nav-item">
                        <a href="#" className="nav-link">
                            Trades
                        </a>
                    </li>
                    <li className="nav-item">
                        <a href="#" className="nav-link">
                            Trades on hold
                        </a>
                    </li>
                    <li className="nav-item">
                        <a href="#" className="nav-link">
                            <img
                                width="35px"
                                height="35px"
                                src={userSvg}
                                alt="img for user profile"
                            />
                        </a>
                    </li>
                    <li className="nav-item">
                        <a href="/auth" className="nav-link">
                            Log Out
                        </a>
                        {/* becouse it redirect you, you get logout (the state of the user go back to null on refresh) */}
                    </li>
                </ul>
                <div
                    onClick={() => {
                        mobileMenu();
                    }}
                    className={cls.ham}
                >
                    <span className={cls.b1}></span>
                    <span className={cls.b2}></span>
                    <span className={cls.b3}></span>
                </div>
            </nav>
        </header>
    );
}

export default NavBar;
