import { useEffect, useState } from "react";
import React from "react-dom";
import { getTracker } from "../helpers/getTracker";
import { useCurrentUser } from "./CurrentUserContext";
import NavBar from "./NavBar";

const url = process.env.BACK_END_URL || "http://localhost:8000";


function Holds(props) {
    const [holds, setHolds] = useState([]);
    const [refresh, setRefresh] = useState(false);
    const { currentUser } = useCurrentUser();

    function refreshHolds() {
        setHolds([]);
        refresh ? setRefresh(false) : setRefresh(true);
    }

    useEffect(() => {
        const fetchHolds = async () => {
            try {
                const response = await getTracker(
                    currentUser.token,
                    "holds",
                    url
                );
                if (response.detail !== "Invalid token.") {
                    response.forEach((hold) => {
                        var avg = 1 / hold.price
                        if (avg < 0.1) {
                            var regex = /0+/g
                            var list_regex = avg.toString().match(regex)
                            hold.round = list_regex[1].length + 2
                            // the above code is to know how much decimald the hold need
                        }
                        hold.start_date = hold.start_date.substring(0, 10);
                        // (date.replace("T", " ").replace("Z", "")) for details of the trade
                        setHolds((prevHold) => [...prevHold, hold]);
                    });
                }
            } catch (e) {
                console.log(e);
            }
        };
        fetchHolds();
        // i disable the props.url warning
        // eslint-disable-next-line react-hooks/exhaustive-deps
    }, [currentUser, refresh, props.refreshAll]);


    return (
        <div className="trades">
        <NavBar />
            <button
                onClick={() => {
                    refreshHolds();
                }}
            >
                Refresh
            </button>

            <div class="section">
                <text>Holds</text>
                <table cellspacing="0" class="table">
                    <tr>
                        <th align="center" class="pair">
                            Pair
                        </th>
                        <th align="center" class="start">
                            Start
                        </th>
                        <th align="center" class="holding">
                            Holding
                        </th>
                        <th align="center" class="price">
                            Price
                        </th>
                        <th align="right" class="p-l">
                            Profit/Loss
                        </th>
                        <th align="right" class="prctg">
                            Percentage
                        </th>
                    </tr>

                    {holds.map((hold) => {
                        return (
                            <tr key={hold.id}>
                                <td align="center" class="pair">
                                    <img width="30px" height="30px" src={hold.logo} alt="coin logo" />
                                    {hold.pair}
                                </td>
                                <td align="center" class="start">
                                    {hold.start_date}
                                </td>
                                <td align="center" class="holding">
                                    {hold.holding.toFixed(hold.round)}
                                </td>
                                <td align="right" class="holding">
                                    ${hold.price}
                                </td>
                                <td align="right" class="p-l">
                                    ${(hold.holding * hold.price).toFixed(2)}
                                </td>
                                <td align="right" class="prctg">
                                    {((hold.price - hold.start_price) / hold.start_price * 100).toFixed(2)}%
                                </td>
                            </tr>
                        );
                    })}
                </table>
            </div>
        </div>
    );
}

export default Holds;
