import { useState } from "react";

function NavBar() {

    const [cls, setCls] = useState({
        clsNav: "nav-menu",
        clsHam: "hamburguer"
    })
    

    
    function mobileMenu() {
        if (cls.clsNav === "nav-menu"){
            setCls({
                clsNav: "nav-menu active",
                clsHam: "hamburguer active"
            })
        } else {
            setCls({
                clsNav: "nav-menu",
                clsHam: "hamburguer"
            })
        }
    }
    
    
    const navLink = document.querySelectorAll(".nav-link");

    // navLink.forEach(n => n.addEventListener("click", closeMenu));

    
    
    return (
        <header className="header">
            <nav className="navbar">
                <ul className={cls.clsNav}>

                    <li className="nav-item">
                        <a href="#" className="nav-link">
                            Upload History File
                        </a>
                    </li>
                    <li className="nav-item">
                        <a href="#" className="nav-link">
                            Holdings
                        </a>
                    </li>
                    <li className="nav-item">
                        <a href="#" className="nav-link">
                            Trades
                        </a>
                    </li>
                    <li className="nav-item" id="search">
                        <input placeholder="Search"></input>
                    </li>
                    <li className="nav-item">
                        <a href="#" className="nav-link">
                            <img
                                src="{% static 'tracker/svg/user-svg.svg' %}"
                                alt="img for user profile"
                            />
                        </a>
                    </li>
                    <li className="nav-item">
                        <a href="/auth" className="nav-link">
                            Log Out 
                        </a>
                        {/* becouse it redirect you, you get logout (the state of the user go back to null on refresh) */}
                    </li>
                </ul>
                <div onClick={()=>{mobileMenu()}} class="hola" className={cls.clsHam}>
                    <span className="bar"></span>
                    <span className="bar"></span>
                    <span className="bar"></span>
                </div>
            </nav>
        </header>
    );
}

export default NavBar
