import React from "react-dom";
// import "./Home.css";
import "../index.css"
import NavBar from "./NavBar";
import UploadFile from "./UploadFile";
import {useCurrentUser} from "./CurrentUserContext"
import { useState } from "react";


const url = process.env.BACK_END_URL || "http://localhost:8000";


function Home() {

    const [show, setShow] = useState({
        trades_on_hold: false,
        holds:true,
        trades:false,
    })

    const {currentUser} = useCurrentUser()

    const handleChange=(e)=>{
        e.target.value === "Holds" ? setShow({
            trades_on_hold: false,
            holds:true,
            trades:false,
        }) : e.target.value === "Trades" ? setShow({
            trades_on_hold: false,
            holds:false,
            trades:true,
        }) : setShow({
            trades_on_hold: true,
            holds:false,
            trades:false,
        })
      }



    return (
        <div className="home">
            <NavBar />
            
            <main className="main">
          
            <UploadFile owner_id={currentUser.id} url={url} token={currentUser.token}/>
            </main>
        </div>
    );
}

export default Home;
