import { useEffect, useState } from "react";
import React from "react-dom";
import { getTracker } from "../helpers/getTracker";
import { useCurrentUser } from "./CurrentUserContext";
import Footer from "./Footer";
import NavBar from "./NavBar";


const url = process.env.BACK_END_URL || "http://localhost:8000";

function TradesOnHold() {
    const [trades_on_hold, setTradesOnHold] = useState([]);
    const [refresh, setRefresh] = useState(false);
    const { currentUser } = useCurrentUser();

    function refreshTradesOnHold() {
        setTradesOnHold([]);
        refresh ? setRefresh(false) : setRefresh(true);
    }

    useEffect(() => {
        setTradesOnHold([])
        const fetchTradesOnHold = async () => {
            try {
                const response = await getTracker(
                    currentUser.token,
                    "trades_on_hold",
                    url
                );
                if (response.detail !== "Invalid token.") {
                    response.forEach((trades_on_hold) => {
                        console.log(trades_on_hold);
                        trades_on_hold.date = trades_on_hold.date.substring(0, 10);
                        // (date.replace("T", " ").replace("Z", "")) for details of the trade
                        setTradesOnHold((prevTrade_on_hold) => [...prevTrade_on_hold, trades_on_hold]);
                    });
                }
            } catch (e) {
                console.log(e);
            }
        };
        fetchTradesOnHold();
        // i disable the props.url warning
        // eslint-disable-next-line react-hooks/exhaustive-deps
    }, [currentUser, refresh]);

    console.log(trades_on_hold);

    return (
        <div className="trades">
            <div class="page-wrap">

                <NavBar />

                <div class="section">
                    <text>Trades on Hold</text>
                    <table cellspacing="0" class="table">
                        <tr>
                            <th align="center" class="pair">
                                Pair
                            </th>
                            <th align="center" class="start">
                                Date
                            </th>
                            <th align="right" class="p-l">
                                Profit/Loss
                            </th>
                            <th align="right" class="prctg">
                                Percentage
                            </th>
                        </tr>

                        {trades_on_hold.map((trade) => {
                            return (
                                <tr key={trade.id}>
                                    <td align="center" class="pair">
                                        <img width="30px" height="30px" src={trade.logo} alt="coin logo" />
                                        {trade.pair}
                                    </td>
                                    <td align="center" class="start">
                                        {trade.date}
                                    </td>
                                    <td align="center" class="p-l">
                                        $ {trade.profit_loss.toFixed(2)}
                                    </td>
                                    <td align="center" class="prctg">
                                        % {trade.percentage.toFixed(2)}
                                    </td>
                                </tr>
                            );
                        })}
                    </table>
                </div>
            </div>
            <Footer />
        </div>
    );
}

export default TradesOnHold;
