import React, { useState } from "react";
import { getUserToken } from "../helpers/getUserToken";
// import { getTrades } from "./helpers/getTrades";
import { createUser } from "../helpers/createUser";
import { useCurrentUser } from "./CurrentUserContext";

const url = process.env.BACK_END_URL || "http://localhost:8000";

function Register() {
    const [user, setUser] = useState({
        username: "",
        email: "",
        password: "",
        confirmation: "",
        error: "",
    });

    const { currentUser, saveCurrentUser } = useCurrentUser();

    function handleChange(event) {
        const { name, value } = event.target;
        setUser((prevVal) => ({ ...prevVal, [name]: value }));
    }

    function handleSubmit(event) {
        if (user.password !== user.confirmation) {
            setUser({
                username: user.username,
                email: user.email,
                password: "",
                confirmation: "",
                error: "The passwords don't match",
            });
        } else {
            createUser(user.username, user.email, user.password, url)
            .catch(e=>{
                return {"Error: ": e}
            })
            .then(
                (res) => {
                    console.log(res["Error"])
                    if (res){
                        return {"Error ": res}
                    } else {
                        if (res.token) {
                            console.log("Succesfully registration");
                            console.log(res);
                            saveCurrentUser(
                                res.user.id,
                                res.token,
                                res.user.username,
                                res.user.email
                            );
                        } else {
                            console.log(res.username[0]);
                            let error =
                                res.username[0] || "There has been an error!";
                            setUser({
                                username: user.username,
                                email: user.email,
                                password: "",
                                confirmation: "",
                                error: error,
                            });
                        }

                    }
                }
            );
        }
        event.preventDefault();
    }

    return (
        <div >
            <form onSubmit={handleSubmit}>
                <div>
                    <input
                        type="text"
                        onChange={handleChange}
                        name="username"
                        value={user.username}
                        placeholder="Username"
                    />
                </div>
                <div>
                    <input
                        type="email"
                        onChange={handleChange}
                        name="email"
                        value={user.email}
                        placeholder="Email"
                    />
                </div>
                <div>
                    <input
                        type="password"
                        onChange={handleChange}
                        name="password"
                        value={user.password}
                        placeholder="Password"
                    />
                </div>
                <div>
                    <input
                        type="password"
                        onChange={handleChange}
                        name="confirmation"
                        value={user.confirmation}
                        placeholder="Confirm your password"
                    />
                </div>
                <div className="error-login">{user.error}</div>
                <button class="btn-blue auth">Sign Up</button>
            </form>
        </div>
    );
}

function LogIn() {
    const [user, setUser] = useState({
        username: "",
        password: "",
        error: "",
    });

    const { currentUser, saveCurrentUser } = useCurrentUser();

    function handleChange(event) {
        const { name, value } = event.target;
        setUser((prevVal) => ({ ...prevVal, [name]: value }));
    }

    function handleSubmit(event) {
        getUserToken(user.username, user.password, url)
        .catch(e=> {
            return {"Error": e}
        })
        .then((res) => 
        {   
            if (res.token) {
                console.log("Succesfully loged in");
                // This saveCurrentUser is to set a context var(user)
                saveCurrentUser(
                    res.user.id,
                    res.token,
                    res.user.username,
                    res.user.email
                );
            } else {
                setUser({
                    username: "",
                    password: "",
                    error: "Error in authentication",
                });
                console.log("Error: ", res);
            }
        });
        event.preventDefault();
    }

    return (
        <div>
            <form onSubmit={handleSubmit}>
                <div>
                    <input
                        type="text"
                        onChange={handleChange}
                        name="username"
                        value={user.username}
                        placeholder="Username"
                    />
                </div>
                <div>
                    <input
                        type="password"
                        onChange={handleChange}
                        name="password"
                        value={user.password}
                        placeholder="Password"
                    />
                </div>
                <div className="error-login">{user.error}</div>
                <button class="btn-blue auth">Login</button>
            </form>
        </div>
    );
}

function Auth() {
    const [comp, setComp] = useState({
        register: true,
        login: false,
    });

    function showComp() {
        comp.register
            ? setComp({
                  register: false,
                  login: true,
              })
            : setComp({
                  register: true,
                  login: false,
              });
    }

    if (comp.register) {
        return (
            <div >
                <div className="container-auth">
                    <Register />
                    <div className="textauth">
                        you alreay have an account? <button onClick={showComp}>Login</button>
                    </div>
                </div>
            </div>
        );
    } else {
        return (
            <div >
                <div className="container-auth">
                    <LogIn />
                    <button onClick={showComp}>Register</button>
                </div>
            </div>
        );
    }
}

export default Auth;
