import { useEffect, useState } from "react";
import React from "react-dom";
import { getTrades } from "../helpers/getTrades";
import { getUserToken } from "../helpers/getUserToken";
import { useCurrentUser } from "./CurrentUserContext";



function Trades(props) {
    const [trades, setTrades] = useState(
        [
        ]);
    const [refresh, setRefresh] = useState(false)
    const { currentUser, saveCurrentUser } = useCurrentUser();

    function user() {
        getUserToken("Grego", "V4711gda", "http://localhost:8000")
        .catch(e=> {
            return {"Error": e}
        })
        .then((res) => 
        {   
            if (res.token) {
                console.log("Succesfully loged in");
                // This saveCurrentUser is to set a context var(user)
                saveCurrentUser(
                    res.user.id,
                    res.token,
                    res.user.username,
                    res.user.email
                );
            } else {
                console.log("Error: ", res);
            }
        });
    }

    function refreshTrades() {
        setTrades([])
        refresh ? setRefresh(false) : setRefresh(true)
    };

    useEffect(()=> {
        const fetchTrades = async () => {
            try{
                const response = await getTrades(currentUser.token, props.url)
                if (response.detail !== "Invalid token.") {
                    response.forEach(trade => {
                        setTrades(prevTrade => [...prevTrade, trade])
                    })
                }
            } catch(e) {
                console.log(e);
            }
        }
        fetchTrades();
        // i disable the props.url warning
        // eslint-disable-next-line react-hooks/exhaustive-deps
    }, [currentUser, refresh, props.refreshAll])


    console.log(trades);

    return (
        <div className="trades">
            Trades
            <button
                onClick={() => {
                    refreshTrades();
                }}
            >
                Refresh
            </button>
            <button
                onClick={() => {
                    user();
                }}
            >
                User
            </button>
            <div class="section">
                <text>Trades</text>
                <table cellspacing="0" class="table">
                    <tr>
                        <th align="center" class="pair">
                            Pair
                        </th>
                        <th align="center" class="start">
                            Start
                        </th>
                        <th align="center" class="end">
                            End
                        </th>
                        <th align="right" class="p-l">
                            Profit/Loss
                        </th>
                        <th align="right" class="prctg">
                            Percentage
                        </th>
                    </tr>

                    {trades.map((trade) => {
                        return (
                            <tr key={trade.id}>
                                <td align="center" class="pair" >
                                    {trade.pair}
                                </td>
                                <td align="center" class="start" >
                                    {trade.start}
                                </td>
                                <td align="center" class="end" >
                                    {trade.end}
                                </td>
                                <td align="center" class="p-l" >
                                    {trade.profit_loss}
                                </td>
                                <td align="center" class="prctg" >
                                    {trade.percentage}
                                </td>
                            </tr>
                        );
                    })}
                </table>
            </div>
        </div>
    );
}

export default Trades;
