import React, { useState } from "react";
import { getUserToken } from "../helpers/getUserToken";
// import { getTrades } from "./helpers/getTrades";
import { createUser } from "../helpers/createUser";
import { useCurrentUser } from "./CurrentUserContext";



const url = process.env.BACK_END_URL || "http://localhost:8000";


function Register(){

    const [user, setUser] = useState({
        username: "",
        email: "",
        password: "",
        confirmation: "",
        error: "",
    })

    const { currentUser, saveCurrentUser } = useCurrentUser();


    function handleChange(event) {
        const { name, value } = event.target;
        setUser((prevVal) => ({...prevVal, [name]: value}));
    }
    
    function handleSubmit(event) {
        if (user.password !== user.confirmation){
            setUser({
                username: user.username,
                email: user.email,
                password: "",
                confirmation: "",
                error: "The passwords don't match",
            })
        } else {
            createUser(user.username, user.email, user.password, url)
            .then(res=> {if (res.token) {
                console.log("Succesfully registration");
				console.log(res);
                saveCurrentUser(res.user.id, res.token, res.user.username, res.user.email);
            } else {
                console.log(res[0]);
                setUser({
                    username: user.username,
                    email: user.email,
                    password: "",
                    confirmation: "",
                    error: "There has been a problem",
                })                
            } 
                ;})
        }
        event.preventDefault();
    }

    return(
        <div className="container-auth">
            <h1>Hello {user.username}!</h1>
            <form onSubmit={handleSubmit}>
                <input
                    type="text"
                    onChange={handleChange}
                    name="username"
                    value={user.username}
                    placeholder="Username"
                />
                <input
                    type="email"
                    onChange={handleChange}
                    name="email"
                    value={user.email}
                    placeholder="Email"
                />
                <input
                    type="password"
                    onChange={handleChange}
                    name="password"
                    value={user.password}
                    placeholder="Password"
                />
                <input
                    type="password"
                    onChange={handleChange}
                    name="confirmation"
                    value={user.confirmation}
                    placeholder="Confirm your password"
                />

                <button>Submit</button>
            </form>
            <div id="error-login">{user.error}</div>
        </div>
    )
}

function LogIn(){

    const [user, setUser] = useState({
        username: "",
        password: "",
        error: ""
    });
    
    const { currentUser, saveCurrentUser } = useCurrentUser();
    
    function handleChange(event) {
        const { name, value } = event.target;
        setUser((prevVal) => ({...prevVal, [name]: value}));
    }
    
    function handleSubmit(event) {

        
        getUserToken(user.username, user.password, url).then((res) => {
            if (res.token) {
                console.log("Succesfully loged in");
                saveCurrentUser(res.user.id, res.token, res.user.username, res.user.email);
            } else {
                setUser({
                    username: "",
                    password: "",
                    error: "Error in authentication"
                })
                console.log("Error: ", res);
            }
        });
        event.preventDefault();
    }

    return (
        <div className="container-auth">
            <h1>Hello {user.username}!</h1>
            <form onSubmit={handleSubmit}>
                <input
                    type="text"
                    onChange={handleChange}
                    name="username"
                    value={user.username}
                    placeholder="Username"
                />
                <input
                    type="password"
                    onChange={handleChange}
                    name="password"
                    value={user.password}
                    placeholder="Password"
                />
                <button>Submit</button>
            </form>
            <div id="error-login">{user.error}</div>
        </div>
    );
}




function Auth() {

    const [comp, setComp] = useState({
        register: true,
        login: false
    })
    
    function showComp(){
        comp.register ? setComp({
            register: false,
            login: true
        }) : setComp({
            register: true,
            login: false
        })
    }

    if (comp.register){
        return(
            <div className="auth">
                <Register />
                <button onClick={showComp}>Click Me!</button>
            </div>
        )
    } else {
        return(
            <div className="auth">
                <LogIn />
                <button onClick={showComp}>Click Me!</button>
            </div>
        )
    }
}

export default Auth;
