import "./App.css";
import React, { useState } from "react";
import { getUserToken } from "../actions/getUserToken";
// import { getTrades } from "./helpers/getTrades";


function Auth() {
    const [user, setUser] = useState({
        username: "",
        password: "",
        error: ""
    });

    const url = process.env.BACK_END_URL || "http://localhost:8000";

    // const trades = [];

    // function listTrades() {
    //     getUserToken("grego", "V4711gda", url).then((r) => {
    //         getTrades(r.token, url).then((data) => {
    //             for (let i = 0; i < data.length; i++) {
    //                 trades[i] = data[i];
    //             }
    //         });
    //     });
    // }

    function handleChange(event) {
        const { name, value } = event.target;
        setUser((prevVal) => ({...prevVal, [name]: value}));
    }

    function handleSubmit(event) {
        getUserToken(user.username, user.password, url).then((res) => {
            if (res.token) {
                console.log("Succesfully loged in");
				console.log(res);
				window.location.href = "/"
            } else {
                setUser({
                    username: "",
                    password: "",
                    error: "Error in authentication"
                })
                console.log("Error: ", res);
            }
        });
        event.preventDefault();
    }

    return (
        <div className="container">
            <h1>Hello {user.username}!</h1>
            <form onSubmit={handleSubmit}>
                <input
                    type="text"
                    onChange={handleChange}
                    name="username"
                    value={user.username}
                    placeholder="Username"
                />
                <input
                    type="password"
                    onChange={handleChange}
                    name="password"
                    value={user.password}
                    placeholder="Password"
                />
                <button>Submit</button>
            </form>
            <div id="error-login">{user.error}</div>
        </div>
    );
}

export default Auth;
