// const input = document.getElementById('fileinput');

import { useState } from "react";
import getBase64 from "../helpers/getBase64";
// This will upload the file after having read it
export default function UploadFile(props) {
    const [file, setFile] = useState(null);
    
    let formData = new FormData()
    function handleChange(e) {
        setFile(e[0]);
        // console.log(e.target.value);
        
        // console.log(e.target);
    }
    console.log(formData);

    function handleSubmit(e) {
        e.preventDefault()
    }
    
    function handleImgUpload(event, theFile){
        let formData = new FormData()
        var elfile = getBase64(theFile)
        // formData.append('file', theFile)
        for (var pair of formData.entries()) {
            console.log(pair[0]+ ', ' + pair[1]); 
        }
        fetch(`${props.url}/tracker/`, {
            // Your POST endpoint
            method: "POST",
            headers: {
                Authorization: "Token " + props.token,
                'Accept': 'application/json',
                'Content-Type': 'application/json'
                // 'Content-Type': 'multipart/form-data',
                // Content-Type may need to be completely **omitted**
                // or you may need something
                // "Content-Type":
            },
            body: JSON.stringify({"file": elfile}), // This is your file object
        })
            .then(
                (response) => response.json() // if the response is a JSON object
            )
            .then(
                (success) => console.log(success) // Handle the success response object
            )
            .catch(
                (error) => console.log(error) // Handle the error response object
            );
    }


    return (
        <form onSubmit={handleSubmit}>
            <input type="file" onChange={handleChange}></input>
            <button type="submit" onClick={event => handleImgUpload(event, file)}>Subir</button>
        </form>
    );
}
