import React from "react-dom";
import "./Home.css";
import NavBar from "./NavBar";
import Trades from "./Trades";
import UploadFile from "./UploadFile";
import {useCurrentUser} from "./CurrentUserContext"
import Holds from "./Holds";
import TradesOnHold from "./TradesOnHold";
import { useState } from "react";


const url = process.env.BACK_END_URL || "http://localhost:8000";


function Home() {

    const [show, setShow] = useState({
        trades_on_hold: false,
        holds:true,
        trades:false,
    })

    const {currentUser} = useCurrentUser()

    const handleSelect=(e)=>{
        console.log("HOLA");
        console.log(e.target.value);
        
      }



    return (
        <div className="home">
            <NavBar />
            <select name="select" onChange={handleSelect}>
                <option value="Holds" >Holds</option>
                <option value="Trades">Trades</option>
                <option value="Trades on hold" >Trades on hold</option>
            </select>
            <main className="main">
            {show.trades_on_hold && <TradesOnHold url={url}/>}
            {show.holds && <Holds url={url}/> }
            {show.trades && <Trades url={url}/> }
            <UploadFile owner_id={currentUser.id} url={url} token={currentUser.token}/>
            </main>
        </div>
    );
}

export default Home;
