import React, { useState } from "react";
import { getUserToken } from "../actions/getUserToken";
// import { getTrades } from "./helpers/getTrades";

function Register(props){

    const [register, setRegister] = useState({
        username: "",
        password: "",
        confirmation: "",
        error: "",
    })

    if (props.show) {
        return(
            <div>
                esto eesss el register
            </div>
        )
    } else {
        return(<div></div>)
    }
}

function LogIn(props){
    const [user, setUser] = useState({
        username: "",
        password: "",
        error: ""
    });

    const url = process.env.BACK_END_URL || "http://localhost:8000";

    
    function handleChange(event) {
        const { name, value } = event.target;
        setUser((prevVal) => ({...prevVal, [name]: value}));
    }
    
    function handleSubmit(event) {
        getUserToken(user.username, user.password, url).then((res) => {
            if (res.token) {
                console.log("Succesfully loged in");
				console.log(res);
				window.location.href = "/"
            } else {
                setUser({
                    username: "",
                    password: "",
                    error: "Error in authentication"
                })
                console.log("Error: ", res);
            }
        });
        event.preventDefault();
    }

    if (props.show) {
        return (
            <div className="container">
                <h1>Hello {user.username}!</h1>
                <form onSubmit={handleSubmit}>
                    <input
                        type="text"
                        onChange={handleChange}
                        name="username"
                        value={user.username}
                        placeholder="Username"
                    />
                    <input
                        type="password"
                        onChange={handleChange}
                        name="password"
                        value={user.password}
                        placeholder="Password"
                    />
                    <button>Submit</button>
                </form>
                <div id="error-login">{user.error}</div>
            </div>
        );
    }else {
        return(<div></div>)
    }
}




function Auth() {

    const [comp, setComp] = useState({
        register: true,
        login: false
    })

    function showComp(){
        if (comp.register){
            setComp({
                register: false,
                login: true
            })
        } else {
            setComp({
                register: false,
                login: true
            })
        }
    }

    if (comp.register){
        return(
            <div class="auth">
                <Register show={comp.register}/>
                <button onClick={showComp}>Click Me!</button>
            </div>
        )
    } else {
        return(
            <div class="auth">
                <LogIn show={comp.login}/>
                <button onClick={showComp}>Click Me!</button>
            </div>
        )
    }
}

export default Auth;

