import { useState } from "react";

function NavBar() {

    const [cls, setCls] = useState({
        
        nav: "nav-menu",
        ham: "hamburguer",
        bar: "bar"
    })
    

    
    function mobileMenu() {
        if (cls.nav === "nav-menu"){
            setCls({
                nav: "nav-menu active",
                ham: "hamburguer active",
                // bar: "bar active"
            })
        } else {
            setCls({
                nav: "nav-menu",
                ham: "hamburguer",
                // bar: "bar"
            })
        }
    }
    
    
    const navLink = document.querySelectorAll(".nav-link");

    // navLink.forEach(n => n.addEventListener("click", closeMenu));

    
    
    return (
        <header className="header">
            <nav className="navbar">
                <ul className={cls.nav}>

                    <li className="nav-item">
                        <a href="#" className="nav-link">
                            Upload History File
                        </a>
                    </li>
                    <li className="nav-item">
                        <a href="#" className="nav-link">
                            Holdings
                        </a>
                    </li>
                    <li className="nav-item">
                        <a href="#" className="nav-link">
                            Trades
                        </a>
                    </li>
                    <li className="nav-item" id="search">
                        <input placeholder="Search"></input>
                    </li>
                    <li className="nav-item">
                        <a href="#" className="nav-link">
                            <img
                                src="{% static 'tracker/svg/user-svg.svg' %}"
                                alt="img for user profile"
                            />
                        </a>
                    </li>
                    <li className="nav-item">
                        <a href="/auth" className="nav-link">
                            Log Out 
                        </a>
                        {/* becouse it redirect you, you get logout (the state of the user go back to null on refresh) */}
                    </li>
                </ul>
                <div onClick={()=>{mobileMenu()}} className={cls.ham}>
                    <span className={cls.bar}></span>
                    <span className={cls.bar}></span>
                    <span className={cls.bar}></span>
                </div>
            </nav>
        </header>
    );
}

export default NavBar
