import { useEffect, useState } from "react";
import React from "react-dom";
import { getTracker } from "../helpers/getTracker";
import { useCurrentUser } from "./CurrentUserContext";

function Holds(props) {
    const [holds, setHolds] = useState([]);
    const [refresh, setRefresh] = useState(false);
    const { currentUser, saveCurrentUser } = useCurrentUser();

    function refreshHolds() {
        setHolds([]);
        refresh ? setRefresh(false) : setRefresh(true);
    }

    useEffect(() => {
        const fetchTrades = async () => {
            try {
                const response = await getTracker(
                    currentUser.token,
                    "holds",
                    props.url
                );
                if (response.detail !== "Invalid token.") {
                    response.forEach((hold) => {
                        hold.start = hold.start.substring(0, 10);
                        // (date.replace("T", " ").replace("Z", "")) for details of the trade
                        setHolds((prevTrade) => [...prevTrade, hold]);
                    });
                }
            } catch (e) {
                console.log(e);
            }
        };
        fetchTrades();
        // i disable the props.url warning
        // eslint-disable-next-line react-hooks/exhaustive-deps
    }, [currentUser, refresh, props.refreshAll]);

    console.log(holds);

    return (
        <div className="trades">
            <button
                onClick={() => {
                    refreshHolds();
                }}
            >
                Refresh
            </button>

            <div class="section">
                <text>Holds</text>
                <table cellspacing="0" class="table">
                    <tr>
                        <th align="center" class="pair">
                            Pair
                        </th>
                        <th align="center" class="start">
                            Start
                        </th>
                        <th align="center" class="end">
                            End
                        </th>
                        <th align="right" class="p-l">
                            Profit/Loss
                        </th>
                        <th align="right" class="prctg">
                            Percentage
                        </th>
                    </tr>

                    {holds.map((hold) => {
                        return (
                            <tr key={hold.id}>
                                <td align="center" class="pair">
                                    <img src={hold.logo} alt="coin logo" />
                                    {hold.pair}
                                </td>
                                <td align="center" class="start">
                                    {hold.start}
                                </td>
                                <td align="center" class="holding">
                                    {hold.holding}
                                </td>
                                <td align="center" class="p-l">
                                    {hold.profit_loss}
                                </td>
                                <td align="center" class="prctg">
                                    {hold.percentage}
                                </td>
                            </tr>
                        );
                    })}
                </table>
            </div>
        </div>
    );
}

export default Holds;
