import { useEffect, useState } from "react";
import React from "react-dom";
import { getTrades } from "../helpers/getTrades";
import { getUserToken } from "../helpers/getUserToken";
import { useCurrentUser } from "./CurrentUserContext";




function Trades(props) {
    const [trades, setTrades] = useState([]);

    const { currentUser, saveCurrentUser } = useCurrentUser();

    function user() {
        getUserToken("Grego", "V4711gda", "http://localhost:8000")
        .catch(e=> {
            return {"Error": e}
        })
        .then((res) => 
        {   
            if (res.token) {
                console.log("Succesfully loged in");
                // This saveCurrentUser is to set a context var(user)
                saveCurrentUser(
                    res.user.id,
                    res.token,
                    res.user.username,
                    res.user.email
                    
                );
            } else {
                console.log("Error: ", res);
            }
        });
    }
    // user();

    function fetchTrade(){
        getTrades(currentUser.token, props.url).then((res) => {
            res.forEach((item) => {
                setTrades(prevTrade => [...prevTrade, item])
            });
        });
        console.log(trades);
    }



    function refreshTrades() {
        setTrades([])
        fetchTrade()
    };
    // useEffect(()=>{
    //     refreshTrades()
    // })

    // function ObjTrade(){

    // }

    return (
        <div className="trades">
            Trades
            <button
                onClick={() => {
                    refreshTrades();
                }}
            >
                Refresh
            </button>
            <div class="section">
                <text>Trades</text>
                <table cellspacing="0" class="table">
                    <tr>
                        <th align="center" class="pair">
                            Pair
                        </th>
                        <th align="center" class="start">
                            Start
                        </th>
                        <th align="center" class="end">
                            End
                        </th>
                        <th align="right" class="p-l">
                            Profit/Loss
                        </th>
                        <th align="right" class="prctg">
                            Percentage
                        </th>
                    </tr>

                    {/* {trades.map((trade, i) => {
                        return (
                            <tr>
                                <td align="center" class="pair" key={i}>
                                    {trade.pair}
                                </td>
                                <td align="center" class="start" key={i}>
                                    {trade.start}
                                </td>
                                <td align="center" class="end" key={i}>
                                    {trade.end}
                                </td>
                                <td align="center" class="p-l" key={i}>
                                    {trade.profit_loss}
                                </td>
                                <td align="center" class="prctg" key={i}>
                                    {trade.percentage}
                                </td>
                            </tr>
                        );
                    })} */}
                </table>
            </div>
        </div>
    );
}

export default Trades;
