import React, { useState } from "react";
import { getUserToken } from "../helpers/getUserToken";
// import { getTrades } from "./helpers/getTrades";
import { createUser } from "../helpers/createUser";
import { useCurrentUser } from "./CurrentUserContext";

const url = process.env.BACK_END_URL || "http://localhost:8000";

function Register() {
    const [user, setUser] = useState({
        username: "",
        email: "",
        password: "",
        confirmation: "",
        error: "",
    });

    const { currentUser, saveCurrentUser } = useCurrentUser();

    function handleSubmit(event) {
        if (user.password !== user.confirmation) {
            setUser({
                username: user.username,
                email: user.email,
                password: "",
                confirmation: "",
                error: "The passwords don't match",
            });
        } else {
            createUser(user.username, user.email, user.password, url).then(
                (res) => {
                    if (res.token) {
                        console.log("Succesfully registration");
                        console.log(res);
                        saveCurrentUser(
                            res.user.id,
                            res.token,
                            res.user.username,
                            res.user.email
                        );
                    } else {
                        console.log(res.username[0]);
                        let error =
                            res.username[0] || "There has been an error!";
                        setUser({
                            username: user.username,
                            email: user.email,
                            password: "",
                            confirmation: "",
                            error: error,
                        });
                    }
                }
            );
        }
        event.preventDefault();
    }

    return (
        <div >
            <form onSubmit={handleSubmit}>
                <div>
                    <input
                        type="text"
                        name="username"
                        value={user.username}
                        placeholder="Username"
                    />
                </div>
                <div>
                    <input
                        type="email"
                        name="email"
                        value={user.email}
                        placeholder="Email"
                    />
                </div>
                <div>
                    <input
                        type="password"
                        name="password"
                        value={user.password}
                        placeholder="Password"
                    />
                </div>
                <div>
                    <input
                        type="password"
                        name="confirmation"
                        value={user.confirmation}
                        placeholder="Confirm your password"
                    />
                </div>
                <button>Submit</button>
            </form>
            <div id="error-login">{user.error}</div>
        </div>
    );
}

function LogIn() {
    const [user, setUser] = useState({
        username: "",
        password: "",
        error: "",
    });

    const { currentUser, saveCurrentUser } = useCurrentUser();

    function handleSubmit(event) {
        getUserToken(user.username, user.password, url)
        .catch(e=> {
            return {"Error": e}
        })
        .then((res) => 
        {   
            if (res.token) {
                console.log("Succesfully loged in");
                // This saveCurrentUser is to set a context var(user)
                saveCurrentUser(
                    res.user.id,
                    res.token,
                    res.user.username,
                    res.user.email
                );
            } else {
                setUser({
                    username: "",
                    password: "",
                    error: "Error in authentication",
                });
                console.log("Error: ", res);
            }
        });
        event.preventDefault();
    }

    return (
        <div className="container-auth">
            <form onSubmit={handleSubmit}>
                <div>
                    <input
                        type="text"
                        name="username"
                        
                        placeholder="Username"
                    />
                </div>
                <div>
                    <input
                        type="password"
                        name="password"
                        
                        placeholder="Password"
                    />
                </div>
                <button>Submit</button>
            </form>
            <div id="error-login">{user.error}</div>
        </div>
    );
}

function Auth() {
    const [comp, setComp] = useState({
        register: true,
        login: false,
    });

    function showComp() {
        comp.register
            ? setComp({
                  register: false,
                  login: true,
              })
            : setComp({
                  register: true,
                  login: false,
              });
    }

    if (comp.register) {
        return (
            <div className="auth">
                <div className="container-auth">
                    <Register />
                    <button onClick={showComp}>LogIn</button>
                </div>
            </div>
        );
    } else {
        return (
            <div className="auth">
                <div className="container-auth">
                    <LogIn />
                    <button onClick={showComp}>Register</button>
                </div>
            </div>
        );
    }
}

export default Auth;
