import "./App.css";
import React, { useState } from "react";
import { getUserToken } from "./actions/getUserToken";
import { getTrades } from "./helpers/getTrades";

function App() {
    const [user, setUser] = useState({
        username: "",
        password: "",
    });

    const url = process.env.BACK_END_URL || "http://localhost:8000";

    const trades = [];

    getUserToken("grego", "V4711gda", url).then((r) => {
        getTrades(r.token, url).then((data) => {
            for (let i = 0; i < data.length; i++) {
                trades[i] = data[i];
            }
        });
    });

    function handleChange(event) {
        const { name, value } = event.target;

        setUser((prevValue) => ({ ...prevValue, [name]: value }));
    }

    return (
        <div className="container">
            <h1>
                Hello {user.username} {user.password}
            </h1>
            <form>
                <input
                    onChange={handleChange}
                    name="username"
                    value={user.username}
                    placeholder="Username"
                />
                <input
                    onChange={handleChange}
                    name="password"
                    value={user.password}
                    placeholder="Password"
                />
                <button>Submit</button>
            </form>
        </div>
    );
}

export default App;
